// src-tauri/src/binary_protocol/ultra_fast.rs
use io_uring::IoUring;
use std::sync::atomic::{AtomicU64, Ordering};

const PROTOCOL_VERSION: u32 = 0x00030000;
const RING_SIZE: usize = 65536;

#[repr(C, align(64))]
pub struct UltraFastMessage {
    header: MessageHeader,
    payload: NonNull<u8>,
    checksum: u32,
}

#[repr(C, packed)]
struct MessageHeader {
    magic: u32,           // 0x55464D53 "UFMS"
    version: u16,
    flags: u16,
    timestamp: u64,
    sequence: AtomicU64,
    source_id: u16,
    dest_id: u16,
    msg_type: u16,
    priority: u8,
    compression: u8,
    payload_len: u32,
}

pub struct BinaryProtocol {
    ring: IoUring,
    agents: DashMap<u16, AgentHandle>,
    message_pool: Arc<MessagePool>,
    throughput: Arc<AtomicU64>,
}

impl BinaryProtocol {
    pub fn new() -> Result<Self, Error> {
        let ring = IoUring::builder()
            .setup_sqpoll(2000)
            .setup_iopoll()
            .build(RING_SIZE)?;

        Ok(Self {
            ring,
            agents: DashMap::new(),
            message_pool: Arc::new(MessagePool::new(1024)),
            throughput: Arc::new(AtomicU64::new(0)),
        })
    }

    pub async fn send_message(&self, msg: UltraFastMessage) -> Result<u64, Error> {
        // Zero-copy message sending via io_uring
        let sqe = self.ring.submission().available().next().unwrap();
        sqe.prep_write(msg.payload, msg.header.payload_len as usize, 0);
        sqe.set_user_data(msg.header.sequence.load(Ordering::Relaxed));

        self.ring.submission().sync();
        self.throughput.fetch_add(1, Ordering::Relaxed);

        Ok(msg.header.sequence.load(Ordering::Relaxed))
    }
}
