            ai_session,
            memory_usage: 256 * 1024 * 1024,
            cpu_affinity: Some(self.resource_manager.assign_cores(tab_id)),
            last_active: SystemTime::now(),
        };

        self.tabs.insert(tab_id, Arc::new(RwLock::new(tab)));
        self.persistence.save_tab_state(&tab).await?;

        Ok(tab_id)
    }

    pub async fn switch_tab(&self, tab_id: Uuid) -> Result<(), TabError> {
        // Sub-10ms tab switching
