        // Suspend current tab's background operations
        if let Some(current) = self.active_tab.read().await.as_ref() {
            self.suspend_tab_operations(current).await?;
        }

        // Activate new tab
        self.activate_tab_operations(&tab_id).await?;
        *self.active_tab.write().await = Some(tab_id);

        let elapsed = start.elapsed();
        if elapsed > Duration::from_millis(10) {
