# Enable debug mode if requested
DEBUG_MODE="${CLAUDE_DEBUG:-false}"
VERBOSE_MODE="${CLAUDE_VERBOSE:-false}"

if [[ "$1" == "--debug" ]]; then
    DEBUG_MODE="true"
    shift
    set -x
fi

if [[ "$1" == "--verbose" ]]; then
    VERBOSE_MODE="true"
    shift
fi

# Logging functions - only show in verbose/debug mode or for errors
log_debug() {
    [[ "$DEBUG_MODE" == "true" ]] && echo -e "${DIM}[DEBUG] $1${NC}" >&2
}

log_error() {
    echo -e "${RED}${ERROR} $1${NC}" >&2
}

log_success() {
    [[ "$VERBOSE_MODE" == "true" ]] && echo -e "${GREEN}${SUCCESS} $1${NC}" >&2
}

