#!/usr/bin/env node

/**
 * Claude Code Agent Registration System
 * Registers all 60 custom agents with the Task tool
 */

const fs = require('fs');
const path = require('path');
const os = require('os');

// Load agent registry
const registryPath = path.join(os.homedir(), '.claude', 'agent-registry.json');
const registry = JSON.parse(fs.readFileSync(registryPath, 'utf8'));

// Claude Code configuration directory
const claudeConfigDir = path.join(os.homedir(), '.claude');
const outputStylesDir = path.join(claudeConfigDir, 'output-styles');
const agentConfigFile = path.join(claudeConfigDir, 'custom-agents.json');

// Ensure directories exist
if (!fs.existsSync(outputStylesDir)) {
    fs.mkdirSync(outputStylesDir, { recursive: true });
}

// Generate agent configuration for Claude Code
const agentConfig = {
    version: "2.0.0",
    lastUpdated: new Date().toISOString(),
    agents: registry.agents.map(agent => ({
        type: "agent",
        name: agent.name,
        description: agent.description,
        category: agent.category,
        aliases: agent.aliases || [],
        enabled: agent.enabled,

        // Task tool configuration
        taskConfig: {
            subagent_type: agent.name,
            allowedTools: getToolsForAgent(agent),
            progressMessage: `Running ${agent.name} agent...`,
            source: "custom"
        },

        // Execution configuration
        execution: {
            type: "prompt",
            async: true,
            timeout: 300000, // 5 minutes
            retryOnFailure: true,
            maxRetries: 3
        }
    }))
};

// Define tool access patterns for different agent categories
function getToolsForAgent(agent) {
    const toolPatterns = {
        'command-control': ['Task', 'Read', 'Write', 'Edit', 'MultiEdit', 'Bash', 'WebSearch'],
        'security': ['Read', 'Grep', 'Bash', 'WebSearch', 'Task'],
        'development': ['Read', 'Write', 'Edit', 'MultiEdit', 'Bash', 'Grep', 'Task'],
        'devops': ['Bash', 'Read', 'Write', 'Edit', 'Task'],
        'language': ['Read', 'Write', 'Edit', 'MultiEdit', 'Bash', 'Task'],
        'platform': ['Read', 'Write', 'Edit', 'MultiEdit', 'Bash', 'WebFetch', 'Task'],
        'data-ml': ['Read', 'Write', 'Bash', 'Task'],
        'network': ['Read', 'Edit', 'Bash', 'Task'],
        'hardware': ['Read', 'Bash', 'Task'],
        'planning': ['Read', 'Write', 'WebSearch', 'Task']
    };

    return toolPatterns[agent.category] || ['Read', 'Write', 'Task'];
}

// Write agent configuration
fs.writeFileSync(agentConfigFile, JSON.stringify(agentConfig, null, 2));
console.log(`âœ… Registered ${agentConfig.agents.length} agents to ${agentConfigFile}`);

// Create agent definition files for Claude Code
agentConfig.agents.forEach(agent => {
    const agentDefPath = path.join(outputStylesDir, `${agent.name}-agent.md`);
    const agentDefinition = `---
name: ${agent.name}
type: agent
category: ${agent.category}
aliases: ${agent.aliases.join(', ')}
---

# ${agent.name.toUpperCase()} Agent

${agent.description}

## Usage

\`\`\`python
Task(subagent_type="${agent.name}", prompt="Your task here")
\`\`\`

## Aliases

You can also use any of these aliases:
${agent.aliases.map(alias => `- ${alias}`).join('\n')}

## Category

This agent belongs to the **${agent.category}** category.

## Available Tools

This agent has access to:
${getToolsForAgent(agent).map(tool => `- ${tool}`).join('\n')}
`;

    fs.writeFileSync(agentDefPath, agentDefinition);
});

console.log(`âœ… Created ${agentConfig.agents.length} agent definition files in ${outputStylesDir}`);

// Create a master agent list for quick reference
const agentListPath = path.join(claudeConfigDir, 'available-agents.md');
const agentList = `# Available Claude Code Agents

Total: ${agentConfig.agents.length} agents

## By Category

${Object.entries(
    agentConfig.agents.reduce((acc, agent) => {
        if (!acc[agent.category]) acc[agent.category] = [];
        acc[agent.category].push(agent);
        return acc;
    }, {})
).map(([category, agents]) => `
### ${category.toUpperCase()} (${agents.length})
${agents.map(a => `- **${a.name}**: ${a.description}`).join('\n')}
`).join('\n')}

## Quick Usage

\`\`\`python
# Command & Control
Task(subagent_type="director", prompt="Create strategic plan")
Task(subagent_type="projectorchestrator", prompt="Coordinate workflow")

# Security
Task(subagent_type="security", prompt="Audit for vulnerabilities")
Task(subagent_type="cryptoexpert", prompt="Implement encryption")

# Development
Task(subagent_type="architect", prompt="Design system architecture")
Task(subagent_type="debugger", prompt="Find root cause of error")

# Language-Specific
Task(subagent_type="python-internal", prompt="Python implementation")
Task(subagent_type="rust-internal", prompt="Rust systems code")

# And 50+ more agents...
\`\`\`
`;

fs.writeFileSync(agentListPath, agentList);
console.log(`âœ… Created agent list at ${agentListPath}`);

console.log('\nğŸ¯ Agent Registration Complete!');
console.log('All 60 agents are now registered with Claude Code\'s Task tool.');
console.log('\nYou can now use:');
console.log('  Task(subagent_type="director", prompt="...")');
console.log('  Task(subagent_type="security", prompt="...")');
console.log('  ...and all other registered agents');

