"""
Test script to verify agent registration with Claude Code Task tool
"""

import json
import os

def test_agent_registration():
    """Test if agents are properly registered"""

    # Check if configuration files exist
    config_files = {
        'settings': '/home/ubuntu/.claude/settings.json',
        'registry': '/home/ubuntu/.claude/agent-registry.json',
        'custom': '/home/ubuntu/.claude/custom-agents.json'
    }

    print("ğŸ” Checking agent registration files...")
    for name, path in config_files.items():
        if os.path.exists(path):
            print(f"âœ… {name}: {path}")
            with open(path, 'r') as f:
                data = json.load(f)
                if 'agents' in data:
                    print(f"   â†’ {len(data['agents'])} agents registered")
        else:
            print(f"âŒ {name}: {path} NOT FOUND")

    # Load and display available agents
    if os.path.exists(config_files['custom']):
        with open(config_files['custom'], 'r') as f:
            config = json.load(f)

        print(f"\nğŸ“‹ Registered Agents: {len(config['agents'])}")
        print("\nğŸ¯ Available via Task tool:")

        # Group by category
        categories = {}
        for agent in config['agents']:
            cat = agent['category']
            if cat not in categories:
                categories[cat] = []
            categories[cat].append(agent['name'])

        for category, agents in sorted(categories.items()):
            print(f"\n{category.upper()} ({len(agents)}):")
            for agent in sorted(agents):
                print(f'  Task(subagent_type="{agent}", prompt="...")')

    print("\nâœ¨ Agent Registration Complete!")
    print("\nUsage Examples:")
    print('  Task(subagent_type="director", prompt="Create strategic plan")')
    print('  Task(subagent_type="security", prompt="Audit vulnerabilities")')
    print('  Task(subagent_type="architect", prompt="Design system")')
    print('  Task(subagent_type="python-internal", prompt="Python implementation")')
    print("\n...and 54+ more agents available!")

if __name__ == "__main__":
    test_agent_registration()

